function home(name: string, params?: object) {
  try {
    let item = {
      name: name,
      path: name,
      params: params,
      key: name + generateRandom()
    };

    _navigator.dispatch(
      CommonActions.reset({
        index: 0,
        routes: [item]
      })
    );
