}
4.路由替换
/**
 * 将栈顶的路由替换为${name}路由
 * @param name 路由名称
 * @param params 路由参数
 */
function replace(name: string, params?: any) {
  let routers = _navigator.getRootState().routes || [];
  let index = indexOfRouteByName(name);
  if (name && index > -1) {
    let item = {
      index,
      name: name,
      path: name,
      key: routers[index].key
    };
    if (index > -1) routers = routers.slice(0, index);
    routers.push(item);
    _navigator.dispatch(
      CommonActions.reset({
        index: 0,
        routes: routers
      })
    );
  } else {
    routers.pop();
    let item = {
      name: name,
      params: params,
      path: name,
      key: name + generateRandom()
    };
    routers.push(item);
    _navigator.dispatch(
      CommonActions.reset({
        index: 0,
        routes: routers
      })
    );
  }
  logInfo('replace', name);
}
